import React, { useState } from 'react';
import './HomeScreen.css';
import threadsData from './data/threads.json';

const TrendingThreads = () => {
  const trendingThreads = [
    { id: 1, title: 'Thread 1', comments: 10 },
    { id: 2, title: 'Thread 2', comments: 5 },
    { id: 3, title: 'Thread 3', comments: 15 },
  ];

  return (
    <div>
      <h2>Trending Threads</h2>
      {trendingThreads.map((thread) => (
        <div key={thread.id}>
          <h3>{thread.title}</h3>
          <p>Comments: {thread.comments}</p>
        </div>
      ))}
    </div>
  );
};

const LatestThreads = () => {
  const latestThreads = [
    { id: 4, title: 'Thread 4', comments: 3 },
    { id: 5, title: 'Thread 5', comments: 8 },
    { id: 6, title: 'Thread 6', comments: 2 },
  ];

  return (
    <div>
      <h2>Latest Threads</h2>
      {latestThreads.map((thread) => (
        <div key={thread.id}>
          <h3>{thread.title}</h3>
          <p>Comments: {thread.comments}</p>
        </div>
      ))}
    </div>
  );
};

const HomeScreen = () => {
  const [showTrending, setShowTrending] = useState(true);

  const handleToggle = () => {
    setShowTrending(!showTrending);
  };

  return (
    <div>
      <div className="heading-container">
        <h1>Current Threads</h1>
        <div className="toggle-container">
          <div className={`toggle ${showTrending ? 'left' : 'right'}`} onClick={handleToggle}>
            <div className="toggle-switch"></div>
          </div>
        </div>
      </div>

      {showTrending ? <TrendingThreads /> : <LatestThreads />}
    </div>
  );
};

export default HomeScreen;
